INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31045, 'Triamcinolone', 'Rilonacept', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31098/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Info