INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31099, 'Triamcinolone', 'Ticlopidine', 'Minor', 'Limited data have shown that corticosteroids may significantly decrease ticlopidine-induced prolongations in bleeding times. The mechanism may be related to corticosteroid induced peripheral vasoconstriction as limited data have shown that corticosteroids do not affect ADP-mediated platelet aggregation in patients who are taking therapeutic doses of ticlopidine. The clinical relevance of this interaction is unknown, although it is suggested by some experts that systemic corticosteroids could be used prior to emergency surgery in patients who are taking ticlopidine. It is unknown whether the risk of thrombosis is increased in patients who are taking both drugs. More data are needed.', NULL, '-', NULL, '-', 'Others', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, More', 'Mometasone furoate, Amcinonide, Budesonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mupirocin, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31152/', '[1] Thebault J, Blatrix C, Blanchard J, Panak E "A possible method to control prolongations of bleeding time under antiplatelet therapy with ticlopidine." Thromb Haemost 48 (1982): 6-8[2] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767369485);
