INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (31409, 'Ritonavir', 'Armodafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, '-', NULL, '-', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Pemoline, Lisdexamfetamine, Solriamfetol, Caffeine, Dexmethylphenidate, Citicoline', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/31462/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767369485);
