INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15501, 'Furosemide', 'Cisplatin', 'Moderate', 'The concomitant use of loop diuretics with cisplatin may increase the risk of ototoxicity. Loop diuretics, especially ethacrynic acid and high doses of furosemide, and cisplatin have been individually associated with ototoxicity.', NULL, 'This combination should be avoided unless the potential benefit outweighs the risk.', NULL, 'This combination should be avoided unless the potential benefit outweighs the risk. Audiometric testing is recommended if these drugs are given concurrently. Patients should be advised to promptly notify their physician if they experience hearing loss or tinnitus.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15516/', '[1] Rhodes T, Twentyman PR "A study of ethacrynic acid as a potential modifier of melphalan and cisplatin sensitivity in human lung cancer parental and drug-resistant cell lines." Br J Cancer 65 (1992): 684-90[2] "Product Information. Edecrin (ethacrynic acid)." Merck & Co, Inc, West Point, PA.[3] Brock PR, Knight KR, Freyer DR, et al. "Platinum-induced ototoxicity in children: a consensus review on mechanisms, predisposition, and protection, including a new International Society of Pediatric Oncology Boston ototoxicity scale." J Clin Oncol 30 (2012): 2408-17[4] Langer T, am Zehnhoff-Dinnesen A, Radtke S, Meitert J, Zolk O "Understanding platinum-induced ototoxicity." Trends Pharmacol Sci 34 (2013): 458-69[5] Parsons SK, Neault MW, Lehmann, et al "Severe ototoxicity following carboplatin-containing conditioning regimen for autologous marrow transplantation for neuroblastoma." Bone Marrow Transplan