INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15530, 'Furosemide', 'Empagliflozin', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the diuretic and hypotensive effects of loop diuretics. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, dehydration, acute kidney injury, and renal function impairment can occur after initiating treatment with SGLT-2 inhibitors, and the risk is increased with concomitant use of diuretics.', NULL, 'Caution is advised during concomitant use of SGLT-2 inhibitors and loop diuretics, particularly in the elderly and patients with impaired renal function.', NULL, 'Caution is advised during concomitant use of SGLT-2 inhibitors and loop diuretics, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. Blood glucose should also be monitored more closely. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15545/', '[1] "Product Information. Jardiance (empagliflozin)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Invokana (canagliflozin)." Janssen Pharmaceuticals, Titusville, NJ.[3] Cerne