INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15832, 'Gemcitabine', 'Cisplatin', 'Minor', 'In vitro and clinical studies have shown concurrent use of gemcitabine and cisplatin to have a synergistic effect. The precise mechanism of the interaction is unknown. The clearance of cisplatin is reduced during combination use. The clinical implications of these findings are unknown. Caution for increases in toxicity may be appropriate.', NULL, '-', NULL, '-', 'Synergism, Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15847/', '[1] Peters GJ, Bergman AM, Ruiz van Haperen VW, Veerman G, Kuiper CM, Braakhuis BJ "Interaction between cisplatin and gemcitabine in vitro and in vivo." Semin Oncol 22 (4 supp (1995): 72-9[2] "Product Information. Gemzar (gemcitabine)." Lilly, Eli and Company, Indianapolis, IN.[3] Nakamura H, Yamaji Y, Fujita T, Fujita J, Takahara J "Synergistic growth inhibition of gemcitabine/cisplatin, and gemcitabine/etoposide on a human lung cancer cell line, RERF-LC-OK (Meeting abstract)." Proc Annu Meet Am Assoc Cancer Res 36 (1995): a24251995', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15833, 'Gemcitabine', 'Cladribine', 'Major', 'The use of cladribine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Cladribine alone may cause severe and prolonged myelosuppression, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible. Acute short-term therapy with corticosteroids can be administered. Caution is advised if IV cladribine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. In patients who have previously been treated with immunomodulatory or immunosuppressive drugs, consider potential additive effect, mode of action, and duration of effect of the other drugs prior to initiation of cladribine. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15848/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc, Raritan, NJ.[3] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc, Raritan, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15834, 'Gemcitabine', 'Clofarabine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15849/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767365203);
