INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15925, 'Gemifloxacin', 'Deutetrabenazine', 'Moderate', 'Deutetrabenazine has been associated with modest QT interval prolongation. In addition, central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking deutetrabenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of deutetrabenazine with other drugs that can prolong the QT interval should generally be avoided. Deutetrabenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. If concomitant use is required, assessment of baseline QT interval and periodic monitoring during therapy may be considered. In addition, dosage reduction may be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor, or in patients who are poor metabolizers of CYP450 2D6.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15940/', '[1] "Product Information. Austedo (deutetrabenazine)." Teva Pharmaceuticals USA, North Wales, PA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15926, 'Gemifloxacin', 'Didanosine', 'Moderate', 'INTERVAL: Concomitant administration with didanosine buffered tablets or pediatric oral solution may reduce the oral bioavailability of gemifloxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine. The pharmacokinetics of gemifloxacin have not been studied in combination with the various didanosine formulations, but significant reductions in bioavailability have been reported for ciprofloxacin, another quinolone, in interaction studies with didanosine.', NULL, 'Gemifloxacin should be administered at least 2 hours before or 3 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', NULL, 'Gemifloxacin should be administered at least 2 hours before or 3 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15941/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] Deppermann KM, Lode H "Fluoroquinolones: interaction profile during enteral absorption." Drugs 45 Suppl 3 (1993): 65-72[3] Polk RE "Drug-drug interactions with ciprofloxacin and other fluoroquinolones." Am J Med 87 (1989): s76-81[4] Damle BD, Mummaneni V, Kaul S, Knupp C "Lack of Effect of Simultaneously Administered Didanosine Encapsulated Enteric Bead Formulation (Videx EC) on Oral Absorption of Indinavir, Ketoconazole, or Ciprofloxacin." Antimicrob Agents Chemother 46 (2002): 385-91[5] Knupp CA, Barbhaiya RH "A multiple-dose pharmacokinetic interaction study between didanosine (videx(r)) and ciprofloxacin (cipro(r)) in male subjects seropositive for HIV but asymptomatic." Biopharm Drug Dispos 18 (1997): 65-77[6] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[7] Marchbanks CR "Drug-drug interactions with fluoroquinolones." Pharmacotherapy 13 (1993): s23-8[8] Sahai J, Gallicano K, Oliveras L, Khaliq S, Hawley-Foss N, Garber G "Cations in the didanosine tablet reduce ciprofloxacin bioavailability." Clin Pharmacol Ther 53 (1993): 292-7[9] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.', 1767365203);
