INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15959, 'Gemtuzumab ozogamicin', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Moderate', 'There are no data regarding the use of live, attenuated cholera vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', NULL, 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15974/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.[3] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767365203);
