INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16217, 'Indinavir', 'Alfuzosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of alfuzosin, which is primarily metabolized by the isoenzyme. Severe hypotension and priapism may occur.', NULL, 'Concomitant use of alfuzosin with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated.', NULL, 'Concomitant use of alfuzosin with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated. Some authorities recommend avoiding concomitant use of alfuzosin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16232/', '[1] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16218, 'Inotuzumab ozogamicin', 'Alfuzosin', 'Moderate', 'Inotuzumab ozogamicin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of inotuzumab ozogamicin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of inotuzumab ozogamicin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use cannot be avoided or if inotuzumab ozogamicin is used in patients with other risk factors for QT prolongation. The manufacturer suggests obtaining electrocardiograms and serum electrolytes at baseline and regularly during treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16233/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Besponsa (inotuzumab ozogamicin)." Wyeth Laboratories, Philadelphia, PA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16219, 'Isavuconazonium', 'Alfuzosin', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of alfuzosin, which is primarily metabolized by the isoenzyme. Hypotension, syncope, QT prolongation, and priapism may occur.', NULL, 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if alfuzosin is prescribed in combination with moderate CYP450 3A4 inhibitors. Pharmacologic response to alfuzosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the alfuzosin dosage adjusted as necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, orthostasis, syncope, or tachycardia. Taking alfuzosin at bedtime may minimize the occurrence of orthostatic effects. Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16234/', '[1] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16220, 'Isocarboxazid', 'Alfuzosin', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16235/', '[1] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1992): 106-8[2] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[3] Schulz R, Antonin KH, Hoffmann E, et al "Tyramine kinetics and pressor sensitivity during monoamine oxidase inhibition by selegiline." Clin Pharmacol Ther 46 (1989): 528-36[4] Ban TA "Drug int