INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16387, 'Alfuzosin', 'Selexipag', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16402/', '[1] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[2] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985): 1070-1[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16388, 'Alfuzosin', 'Selpercatinib', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16403/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16389, 'Alfuzosin', 'Sertraline', 'Moderate', 'Alfuzosin may cause modest prolongation of the QTc interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if alfuzosin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if alfuzosin is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS or orthostatic effects (e.g., psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with alfuzosin and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16404/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=