INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16423, 'Alfuzosin', 'Tizanidine', 'Moderate', 'Tizanidine has alpha-adrenergic agonist activities and may potentiate the hypotensive effects of other medications, including alfuzosin. In addition, both alfuzosin and tizanidine may cause prolongation of the QTc interval. Theoretically, coadministration of agents that can prolong the QT interval may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on the QT interval.', NULL, 'Caution and clinical monitoring are recommended during concurrent therapy.', NULL, 'Caution and clinical monitoring are recommended during concurrent therapy. Patients should be advised to notify their physician if they experience orthostatic symptoms (e.g., lightheadedness, dizziness, syncope) and to seek medical help if they experience palpitations, or irregular heartbeats.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16438/', '[1] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.[2] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.[3] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.', 1767365203);
