INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16459, 'Acetylcholine', 'Phenyltoloxamine', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16474/', '[1] "Product Information. Pilopine HS (pilocarpine ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Multum Information Services, Inc. Expert Review Panel"', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16460, 'Aclidinium', 'Phenyltoloxamine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16475/', '[1] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc., Menlo Park, CA.[2] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[5] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[6] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals, St. Louis, MO.[7] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim, Ridgefield, CT.[8] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012): 1717-21[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16461, 'Acrivastine', 'Phenyltoloxamine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16476/', '[1] Kulik AV, Wilbur R "Delirium and stereotypy from anticholinergic antiparkinson drugs." Prog Neuropsychopharmacol Biol Psychiatry 6 (1982): 75-82[2] Mann SC, Boger WP "Psychotropic drugs, summer heat and humidity, and hyperplexia: a danger restated." Am J Psychiatry 135 (1978): 1097-100[3] Warnes H, Lehmann HE, Ban TA "Adynamic ileus during psychoactive medication: a report of three fatal and five severe cases." Can Med Assoc J 96 (1967): 1112-3[4] Sarnquist F, Larson CP Jr "Drug-induced heat stroke." Anesthesiology 39 (1973): 348-50[5] Gershon S, Neubauer H, Sundland DM "Interaction between some anticholinergic agents and phenothiazines." Clin Pharmacol Ther 6 (1965): 749-56[6] Lee BS "Possibility of hyperpyrexia with antipsychotic and anticholinergic drugs." J Clin Psychiatry 47 (1986): 571[7] Zelman S, Guillan R "Heat stroke in phenothiazine-treated patients: a report of three fatalities." Am J Psychiatry 126 (1970): 1787-90[8] Cohen MA, Alfonso CA, Mosquera M "Development of urinary retention during treatment with clozapine and meclizine [published erratum appears in Am J Psychiatry 1994 Jun;151(6):952]." Am J Psychiatry 151 (1994): 619-20[9] Moreau A, Jones BD, Banno V "Chronic central anticholinergic toxicity in manic depressive illness mimicking dementia." Can J Psychiatry 31 (1986): 339-41[10] "Product Information. Artane (trihexyphenidyl)." Lederle Laboratories, Wayne, NJ.[11] Hvizdos AJ, Bennett JA, Wells BG, Rappaport KB, Mendel SA "Anticholinergic psychosis in a patient receiving usual doses of haloperidol." Clin Pharm 2 (1983): 174-8[12] Stadnyk AN, Glezos JD "Drug-induced heat stroke." Can Med Assoc J 128 (1983): 957-9[13] "Product Information. Cogentin (benztropine)." Merck & Co, Inc, West Point, PA.[