INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333623, 'Trabectedin', 'Trastuzumab emtansine', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333624, 'Trastuzumab emtansine', 'Treprostinil', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 1767317475, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333625, 'Troleandomycin', 'Trastuzumab emtansine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to the cytotoxic component of ado-trastuzumab emtansine known as DM1, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 3A5.', 'DDInter', 1767317475, 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of ado-trastuzumab emtansine during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider delaying initiation of ado-trastuzumab emtansine until therapy with the potent CYP450 3A4 inhibitor is complete and the drug has cleared from the circulation, or approximately 3 elimination half-lives. When concomitant administration is necessary, patients should be closely monitored for adverse reactions such as hepatic impairment, left ventricular dysfunction, peripheral neuropathy, and thrombocytopenia.', 'Metabolism', 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
