INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333666, 'Epinephrine', 'Cocaine (nasal)', 'Major', 'Concomitant use of cocaine with sympathomimetic agents such as epinephrine and phenylephrine may lead to additive cardiovascular effects. The proposed mechanism involves increased circulating catecholamines and increased activity of the sympathetic nervous system.', 'DDInter', 1767317475, 'Concomitant use of cocaine with sympathomimetic agents should be avoided. If coadministration is required, prolonged vital sign and ECG monitoring may be necessary.', 'Synergism', 'Concomitant use of cocaine with sympathomimetic agents should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
