INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333672, 'Epinephrine', 'Desmopressin', 'Moderate', 'Concomitant use of vasopressin or desmopressin with catecholamines is expected to result in an additive effect on mean arterial blood pressure and other hemodynamic parameters. In addition, although the combination noradrenaline (norepinephrine) and vasopressin is clinically used to improve hemodynamic parameters, noradrenaline may also decrease the antidiuretic effect of vasopressin.', 'DDInter', 1767317475, 'The use of injectable vasopressin or large doses of intranasal or oral desmopressin with other pressor agents should only be done with careful hemodynamic monitoring. Vasopressin dosage may need to be adjusted. Consulting local protocols with respect to the dosage of vasopressin and the order of administration when used with noradrenaline is encouraged.', 'Synergism', 'The use of injectable vasopressin or large doses of intranasal or oral desmopressin with other pressor agents should only be done with careful hemodynamic monitoring.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333673, 'Epinephrine', 'Desvenlafaxine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 1767317475, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333674, 'Epinephrine', 'Dexmethylphenidate', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317475, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
