INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333688, 'Epinephrine', 'Entacapone', 'Moderate', 'COMT inhibitors affect the elimination of drugs metabolized by catechol-O-methyltransferase (COMT) and may potentiate their effects. Arrhythmias, increased heart rate, and blood pressure changes may occur. Entacapone has increased heart rates up to 80% when coadministered with isoproterenol and epinephrine. Ventricular tachycardia has also been reported.', 'DDInter', 1767317475, 'The manufacturer recommends that caution be exercised when these drugs are given together. Monitoring for changes in hemodynamic status and ECG is recommended.', 'Metabolism', 'The manufacturer recommends that caution be exercised when these drugs are given together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333689, 'Epoprostenol', 'Epinephrine', 'Moderate', 'Concurrent administration of decongestants or vasoconstrictors may negate the vasodilatory effects of prostacyclin analogs in patients with pulmonary hypertension.', 'DDInter', 1767317475, 'The use of decongestants and vasoconstrictors should probably be avoided in patients with pulmonary hypertension. Patients should be counseled to consult with their physician before taking over-the-counter decongestants such as phenylephrine and pseudoephedrine.', 'Antagonism', 'The use of decongestants and vasoconstrictors should probably be avoided in patients with pulmonary hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333690, 'Epinephrine', 'Ergometrine', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317475, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, NULL, 'Major', 'pharmacodynamic');
