INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1333768, 'Epinephrine', 'Oxytocin', 'Moderate', 'Concomitant use of oxytocic agents with sympathomimetic amines may increase the risk of hypertension due to additive vasoconstrictive effects. Severe hypertension and headache have been reported when oxytocin was given 3 to 4 hours following prophylactic administration of a vasoconstrictor in conjunction with caudal block anesthesia.', 'DDInter', 1767317475, 'Close monitoring of blood pressure is recommended during coadministration.', 'Synergism', 'Close monitoring of blood pressure is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
