INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (16740, 'Aliskiren', 'Ardeparin', 'Moderate', 'The concomitant use of aliskiren and heparin or low molecular weight heparins may increase the risk of hyperkalemia. Both aliskiren and some heparins individually have been associated with increased potassium levels or hyperkalemia. Patients with renal impairment, diabetes, old age, and severe or worsening heart failure may be at a greater risk.', NULL, 'Caution is advised if aliskiren is used with heparin or low molecular weight heparin.', NULL, 'Caution is advised if aliskiren is used with heparin or low molecular weight heparin. It may be advisable to monitor serum potassium and renal function.', 'Synergism', 'Amlodipine, Hydrochlorothiazide, Amlodipine, Valsartan, Irbesartan, Hydrochlorothiazide, Losartan, Olmesartan, Azilsartan medoxomil, Nebivolol, Telmisartan, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16755/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] Tapaninen T, Neuvonen PJ, Niemi M "Grapefruit juice greatly reduces the plasma concentrations of the OATP2B1 and CYP3A4 substrate aliskiren." Clin Pharmacol Ther 88 (2010): 339-42[4] Vaidyanathan S, Jarugula V, Dieterich HA, Howard D, Dole WP "Clinical pharmacokinetics and pharmacodynamics of aliskiren." Clin Pharmacokinet 47 (2008): 515-31[5] Tapaninen T, Neuvonen PJ, Niemi M "Orange and apple juices greatly reduce the plasma concentrations of the OATP2B1 substrate aliskiren." Br J Clin Pharmacol 71 (2010): 718-26[6] "Product Information. Tekturna (aliskiren)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
