INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17012, 'Hydrochlorothiazide', 'Cholestyramine', 'Moderate', 'INTERVAL: Cholestyramine may decrease the gastrointestinal absorption of thiazide diuretics by binding them in the gut. The interaction has been studied with hydrochlorothiazide (HCTZ).', NULL, 'Thiazide diuretics should be administered at least 1 to 2 hours before or 4 to 6 hours after cholestyramine administration.', NULL, 'Thiazide diuretics should be administered at least 1 to 2 hours before or 4 to 6 hours after cholestyramine administration. Patients should be monitored for potentially diminished therapeutic effects, and the thiazide dosage adjusted if necessary.', 'Absorption', 'Felodipine, Enalapril, Amlodipine, Moexipril, Verapamil, Ramipril, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Lomitapide, Bempedoic acid, Colesevelam, Alirocumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17027/', '[1] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[2] "Product Information. Atacand HCT (candesartan-hydrochlorothiazide)" AstraZeneca Pharmaceuticals, Wilmington, DE.[3] Hunninghake DB, Hibbard DM "Influence of time intervals for cholestyramine dosing on the absorption of hydrochlorothiazide." Clin Pharmacol Ther 39 (1986): 329-34[4] Hunninghake DB, King S, LaCroix K "The effect of cholestyramine and colestipol on the absorption of hydrochlorothiazide." Int J Clin Pharmacol Ther Toxicol 20 (1982): 151-4', 1767369485);
