INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17215, 'Hydrochlorothiazide', 'Phenobarbital', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Trichlormethiazide, Methyclothiazide, Sacubitril, Aliskiren, Trichlormethiazide, Aliskiren, Ascorbic acid, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Suvorexant, Nitrazepam, Thiopental, Butalbital, Eszopiclone, Ramelteon, Tasimelteon, Methohexital, Lemborexant, Estazolam, Methylphenobarbital, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17230/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternba