INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17225, 'Hydrochlorothiazide', 'Polyethylene glycol (3350 with electrolytes)', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations. Fluid and electrolyte disturbances can lead to serious adverse events including cardiac arrhythmias, seizures, and renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment. Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered. Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing. Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Amlodipine, Sacubitril, Nebivolol, Amlodipine, Tamsulosin, Tinidazole, Ascorbic acid, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17240/', '[1] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories, Braintree, MA.[2] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories, Braintree, MA.[3] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[5] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17226, 'Hydrochlorothiazide', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', 'Felodipine, Moexipril, Verapamil, Lisinopril, Perindopril, Bisoprolol, Benazepril, Trandolapril, Captopril, Azilsartan medoxomil, Nebivolol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17241/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Photofrin (porfimer)." Sanofi Winthrop Pharmaceuticals, New York, NY.[3] Blakely KM, Drucker AM, Rosen CF "Drug-induced photosensitivityan update: Culprit drugs, prevention and management." Drug Saf 42 (2019): 827-47[4] Hoffman GA, Gradl G, Schulz M, Haidinger G, Tanew A, Weber B "The frequency of photosensitizing drug dispensings in Austria and Germany: A correlation with their photosensitizing potential based on published l