        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1333898, 'Ibuprofen', 'Cyclosporine', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the nephrotoxic effects of cyclosporine, especially if dehydration is present. The exact mechanism is unknown but is apparently unrelated to plasma cyclosporine levels. The interaction has been reported with diclofenac and sulindac.', NULL, 'Renal function should be closely monitored in patients receiving concomitant therapy with cyclosporine and NSAIDs.', NULL, 'Renal function should be closely monitored in patients receiving concomitant therapy with cyclosporine and NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
