        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1333936, 'Ibuprofen', 'Eptifibatide', 'Moderate', 'The risk of bleeding associated with glycoprotein IIb/IIIa inhibitors is increased in the presence of nonsteroidal anti-inflammatory agents due to additive inhibitory effects on platelet function. Arterial puncture sites may be most problematic.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
