    ) VALUES (1333967, 'Ibuprofen', 'Hydralazine', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of hydralazine and dihydralazine. This interaction suggests that prostaglandins play a pivotal role in the vasodilatory effects of hydralazine.', NULL, 'Recommended management consists of monitoring the antihypertensive effectiveness of hydralazine or dihydralazine during therapy, increasing the dosage as necessary, or decreasing or substituting another drug for the NSAID.', NULL, 'Recommended management consists of monitoring the antihypertensive effectiveness of hydralazine or dihydralazine during therapy, increasing the dosage as necessary, or decreasing or substituting another drug for the NSAID.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
