    ) VALUES (1334014, 'Ibuprofen', 'Magnesium hydroxide', 'Minor', 'One study has suggested that coadministration of magnesium hydroxide and ibuprofen may significantly increase the area under the plasma concentration-time curve for ibuprofen. This increase may be clinically beneficial in circumstances in which a rapid response to ibuprofen is desirable.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
