    ) VALUES (1334116, 'Ibuprofen', 'Tacrine', 'Minor', 'An isolated case has been reported in which delirium was associated with the combined use of tacrine and ibuprofen. The mechanism of this interaction has not been determined. When tacrine and ibuprofen are used together, clinical monitoring for evidence of delirium may be warranted.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
