INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334358, 'Ethinylestradiol', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones. The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317475, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334359, 'Ethinylestradiol', 'Atorvastatin', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins. The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
