INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334421, 'Clobazam', 'Levonorgestrel', 'Moderate', 'RECOMMENDED: Coadministration with clobazam, a weak inducer of CYP450 3A4, may decrease the plasma concentrations of contraceptive hormones.', 'DDInter', 1767317475, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with clobazam. Additional, nonhormonal forms of contraception should be used during and for 28 days after discontinuing clobazam treatment to ensure contraceptive reliability. A significant interaction with hormone-releasing intrauterine systems is not anticipated due to their local action. Injectable progestin-only contraceptives are also thought to be unaffected by enzyme-inducing drugs. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with clobazam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
