INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334427, 'Ethinylestradiol', 'Clozapine', 'Moderate', 'Limited data suggest that coadministration with contraceptive hormones may increase the plasma concentrations of clozapine. The exact mechanism of interaction is unknown but may involve inhibition of clozapine metabolism via CYP450 1A2, 2C19 and/or 3A4.', 'DDInter', 1767317475, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms. Pharmacologic response to clozapine should be monitored more closely whenever an oral contraceptive is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the clozapine dosage adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334428, 'Cobicistat', 'Levonorgestrel', 'Moderate', 'RECOMMENDED: Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones. The exact mechanism of interaction has not been described. Clinical effects of increased progestogen levels are not fully known, but may include increased risk of insulin resistance, dyslipidemia, acne, and venous thrombosis.', 'DDInter', 1767317475, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis. Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended. Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide. However, the long term effects of increased progesterone exposure are not known. Alternative, nonhormonal methods of contraception may be considered.', 'Others', 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334429, 'Ethinylestradiol', 'Colesevelam', 'Moderate', 'INTERVAL: Colesevelam may decrease the gastrointestinal absorption of contraceptive hormones.', 'DDInter', 1767317475, 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose. The same precaution may be applicable to other contraceptive hormones, although data are lacking.', 'Absorption', 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334430, 'Colestipol', 'Levonorgestrel', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins. The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine. In addition, it has also been suggested that bile-acid binding resins may interfere with the enterohepatic recycling of estrogens and therefore increase their elimination.', 'DDInter', 1767317475, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam. Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
