INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1334453, 'Ethinylestradiol', 'Dicoumarol', 'Moderate', 'Concomitant therapy with estrogen-containing drugs may diminish the therapeutic effects of oral anticoagulants. Estrogens can increase the plasma levels of certain clotting factors such as fibrinogen, prothrombin, and factors VII and VIII in a dose-dependent manner, resulting in increased risk of thromboembolism, stroke, and/or myocardial infarction. The risk may be further increased by lifestyle choices such as smoking and lack of exercise.', 'DDInter', 1767317475, 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks. Close clinical and laboratory monitoring are recommended if the combination is prescribed. Patients should be advised to promptly notify their physician if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Antagonism', 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
