INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17600, 'Allopurinol', 'Mephenytoin', 'Minor', 'Limited clinical data suggest that allopurinol may increase phenytoin serum concentrations. The mechanism is unknown and causality is unclear. It is not known whether ethotoin and mephenytoin, which are similar hydantoin compounds, also participate in this interaction. The patient should be monitored for signs of phenytoin toxicity when allopurinol is added to an established phenytoin regimen. Patients should be advised to notify their physician if they experience symptoms of possible hydantoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17615/', '[1] Yokochi K, Yokochi A, Chiba K, Ishizaki T "Phenytoin-allopurinol interaction: Michaelis-Menten kinetic parameters of phenytoin with and without allopurinol in a child with Lesch-Nyhan syndrome." Ther Drug Monit 4 (1982): 353-7[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[6] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[7] "Product Information. Dilantin (phe