INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17624, 'Almotriptan', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', NULL, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17639/', '[1] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17625, 'Almotriptan', 'Armodafinil', 'Minor', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic. Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers). However, the potential for interaction should be considered with any drug metabolized by CYP450 3A4, especially given the high degree of interpatient variability with respect to CYP450-mediated metabolism. Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy. Dosage adjustments may be required if an interaction is suspected.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17640/', '[1] Robertson P Jr, Hellriegel ET, Arora S, Nelson M "Effect of modafinil on the pharmacokinetics of ethinyl estradiol and triazolam in healthy volunteers." Clin Pharmacol Ther 71 (2002): 46-56[2] Doherty MM, Charman WN "The mucosa of the small intestine: how clinically relevant as an organ of drug metabolism?" Clin Pharmacokinet 41 (2002): 235-53[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000): 664-71[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[11] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17626, 'Almotriptan', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of almotriptan, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The possibility of prolonged and/or increased pharmacologic effects of almotriptan, including serious adverse effects such as vasospastic reactions, should be considered during concomitant therapy. Patients should be advised to notify their physician if they experience nausea; pain or tightness in the chest, neck, or jaw; irregular heartbeat; abnormal breathing; seizures; tremor; and/or tingling or numbness.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17641/', '[1] "Product Information. Axert (almotriptan)" Pharmacia and Upjohn, Kalamazoo, MI.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
