INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17660, 'Idelalisib', 'Almotriptan', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of almotriptan, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The possibility of prolonged and/or increased pharmacologic effects of almotriptan, including serious adverse effects such as vasospastic reactions, should be considered during concomitant therapy. Patients should be advised to notify their physician if they experience nausea; pain or tightness in the chest, neck, or jaw; irregular heartbeat; abnormal breathing; seizures; tremor; and/or tingling or numbness.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17675/', '[1] "Product Information. Axert (almotriptan)" Pharmacia and Upjohn, Kalamazoo, MI.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17661, 'Imatinib', 'Almotriptan', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', NULL, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17676/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363020);
