INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17679, 'Almotriptan', 'Mifepristone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of almotriptan, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Caution is advised if almotriptan is used in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The possibility of prolonged and/or increased pharmacologic effects of almotriptan, including serious adverse effects such as vasospastic reactions, should be considered during concomitant therapy. Patients should be advised to notify their physician if they experience nausea; pain or tightness in the chest, neck, or jaw; irregular heartbeat; abnormal breathing; seizures; tremor; and/or tingling or numbness.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17694/', '[1] "Product Information. Axert (almotriptan)" Pharmacia and Upjohn, Kalamazoo, MI.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17680, 'Almotriptan', 'Milnacipran', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'In general, the use of milnacipran in combination with other serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the use of milnacipran in combination with other serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17695/', '[1] Tissot TA "Probable meperidine-induced serotonin syndrome in a patient with a history of fluoxetine use." Anesthesiology 98 (2003): 1511-1512[2] Smith DL, Wenegrat BG "A case report of serotonin syndrome associated with combined nefazodone and fluoxetine." J Clin Ps