INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (17972, 'Capmatinib', 'Metformin', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of multidrug and toxin extrusion 1 and 2K (MATE1 and MATE2K) proteins, such as methylnaltrexone and tipiracil. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of MATE1 and/or MATE2K.', NULL, 'Coadministration of capmatinib with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided.', NULL, 'Coadministration of capmatinib with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and the dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/17987/', '[1] "Product Information. Tabrecta (capmatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767363020);
