        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334497, 'Fostemsavir', 'Ethinylestradiol', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of ethinyl estradiol. The mechanism of interaction has not been described.', NULL, 'The daily dose of ethinyl estradiol should not exceed 30 mcg when used with fostemsavir.', NULL, 'The daily dose of ethinyl estradiol should not exceed 30 mcg when used with fostemsavir. Caution is advised during coadministration, particularly in patients with additional risk factors for thromboembolic events.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
