    ) VALUES (1334510, 'Halazepam', 'Ethinylestradiol', 'Minor', 'Oral contraceptives may prolong the half-life of certain benzodiazepines that undergo hepatic oxidative metabolism (such as diazepam and chlordiazepoxide). Other benzodiazepines also may participate in this interaction. Close observation for signs and symptoms of altered benzodiazepine effect is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
