        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334511, 'Hemin', 'Ethinylestradiol', 'Major', 'Coadministration with drugs that are capable of increasing the activity of delta-aminolevulinic acid synthetase (e.g., estrogens, barbiturates, steroid metabolites, rifampin) may negate the pharmacologic effect of hemin. In the treatment of porphyria, hemin is thought to limit the rate of porphyrin/heme biosynthesis by inhibiting delta-aminolevulinic acid synthetase, thus any induction of this enzyme would be expected to counteract the therapeutic effect of hemin.', NULL, 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', NULL, 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
