    ) VALUES (1334515, 'Imipramine', 'Ethinylestradiol', 'Minor', 'The bioavailability and serum concentration of tricyclic antidepressants may be increased in women taking oral contraceptives. The mechanism may be related to inhibition of the hepatic metabolism of tricyclics.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
