        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334558, 'Ethinylestradiol', 'Lomitapide', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 including oral contraceptives may increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme. Weak CYP450 3A4 inhibitors increase lomitapide exposure by approximately 2-fold according to the product labeling.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives. Likewise, if oral contraceptives are initiated during treatment with lomitapide, the dosage of lomitapide may require adjustment. Patients should be advised that hormone absorption from oral contraceptives may be incomplete if vomiting or diarrhea occurs while taking lomitapide, which would warrant the use of additional contraceptive methods, especially since lomitapide may be teratogenic.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
