    ) VALUES (1334570, 'Ethinylestradiol', 'Metoprolol', 'Moderate', 'Oral contraceptives containing ethinyl estradiol may interfere with the first-pass metabolism of certain beta-blockers and increase their plasma concentrations.', NULL, 'Pharmacologic effects including blood pressure and heart rate changes should be monitored more closely when metoprolol, oxprenolol, and propranolol are coadministered with ethinyl estradiol oral contraceptives, and dosage adjustments made as necessary.', NULL, 'Pharmacologic effects including blood pressure and heart rate changes should be monitored more closely when metoprolol, oxprenolol, and propranolol are coadministered with ethinyl estradiol oral contraceptives, and dosage adjustments made as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
