        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334649, 'Ethinylestradiol', 'Roflumilast', 'Moderate', 'Coadministration with oral contraceptives may increase the systemic exposure to roflumilast and its pharmacologically active N-oxide metabolite.', NULL, 'Caution is advised when roflumilast is used in combination with oral contraceptives containing ethinyl estradiol and/or gestodene.', NULL, 'Caution is advised when roflumilast is used in combination with oral contraceptives containing ethinyl estradiol and/or gestodene. Treatment with roflumilast should be re-evaluated if an interaction is suspected and persistent intolerability occurs. Patients should be advised to contact their doctor if they experience increased frequency and/or severity of side effects such as diarrhea, nausea, abdominal pain, weight loss, headache, insomnia, depression, and anxiety.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
