        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334650, 'Ethinylestradiol', 'Romidepsin', 'Moderate', 'RECOMMENDED: Romidepsin may reduce the effectiveness of estrogen-containing contraceptives. The proposed mechanism is competition by romidepsin for binding to estrogen receptors, which has been demonstrated in vitro.', NULL, 'Estrogen-containing contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with romidepsin.', NULL, 'Estrogen-containing contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with romidepsin. Female patients of childbearing potential should consider using an additional method of contraception if possible. Input from a gynecologist or similar expert on adequate contraception should be sought as needed.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
