    ) VALUES (1334653, 'Rosuvastatin', 'Levonorgestrel', 'Minor', 'Coadministration with rosuvastatin may increase the plasma concentrations of certain oral contraceptive hormones. The mechanism of interaction has not been established. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive doses.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
