    ) VALUES (1334677, 'Sugammadex', 'Levonorgestrel', 'Major', 'RECOMMENDED: Sugammadex may decrease the free plasma concentrations and therapeutic efficacy of hormonal contraceptives.', NULL, 'Female patients of reproductive potential receiving hormonal contraceptives (whether oral or non-oral) should use an additional, nonhormonal contraceptive method or backup method of contraception (e.', NULL, 'Female patients of reproductive potential receiving hormonal contraceptives (whether oral or non-oral) should use an additional, nonhormonal contraceptive method or backup method of contraception (e.g., condoms and spermicides) for 7 days after sugammadex administration.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
