        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1334729, 'Ethinylestradiol', 'Zolmitriptan', 'Minor', 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives. The mechanism and clinical significance are unknown. The effect of zolmitriptan on the pharmacokinetics of oral contraceptives has not been reported. Patients should be advised to notify their physician promptly if they experience sedation, dizziness, paresthesia, chest pain or tightness, shortness of breath, or irregular heartbeats.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
