    ) VALUES (1334730, 'Amiodarone', 'Agalsidase beta', 'Major', 'Amiodarone, chloroquine, and gentamicin may have the potential to inhibit intracellular alpha-galactosidase activity, thereby reducing the pharmacologic effects of agalsidase alfa and agalsidase beta. Clinical data have not been reported.', NULL, 'Agalsidase should not be coadministered with amiodarone, chloroquine, or gentamicin.', NULL, 'Agalsidase should not be coadministered with amiodarone, chloroquine, or gentamicin.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
