    ) VALUES (1334739, 'Hydrocortisone', 'Alefacept', 'Moderate', 'The use of alefacept with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Patients receiving other immunosuppressive or myelosuppressive agents or phototherapy should not be treated with alefacept because of the possibility of excessive immunosuppression.', NULL, 'Patients receiving other immunosuppressive or myelosuppressive agents or phototherapy should not be treated with alefacept because of the possibility of excessive immunosuppression.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
