INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18019, 'Dofetilide', 'Metformin', 'Moderate', 'The major pathway for elimination of dofetilide is through glomerular filtration and active tubular secretion using the cation transport system. Competition for this elimination pathway by other drugs may potentially result in increased levels of dofetilide and increase the risk of QT interval prolongation and ventricular arrhythmias. The clinician should be aware of the potential for an interaction when dofetilide is used in combination with another medication that is excreted via this pathway.', NULL, 'The manufacturer recommends that the patient be closely monitored if these drugs are coadministered.', NULL, 'The manufacturer recommends that the patient be closely monitored if these drugs are coadministered.', 'Excretion', 'Propafenone, Dronedarone, Tocainide, Mexiletine, Disopyramide, Bretylium, Moricizine, Ibutilide', 'Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, Albiglutide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18034/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
