INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18050, 'Guanfacine', 'Metformin', 'Moderate', 'Coadministration with guanfacine may theoretically increase the plasma concentrations and risk of adverse effects of drugs that are substrates of the hepatic uptake transporter, organic cation transporter 1 (OCT1). The proposed mechanism is decreased clearance due to guanfacine-mediated inhibition of OCT1. Based on in vitro data, guanfacine may be an inhibitor of OCT1 at maximal portal vein concentrations. Concomitant use of guanfacine with OCT1 substrates with a similar time to maximum concentration (Tmax) (e.g., metformin), may increase the maximum plasma concentration (Cmax) of these medicines. The clinical significance of this interaction is unknown.', NULL, 'Caution is advised if guanfacine is used concomitantly with drugs that are substrates of OCT1, such as metformin.', NULL, 'Caution is advised if guanfacine is used concomitantly with drugs that are substrates of OCT1, such as metformin. Dosage adjustment and monitoring may be required whenever guanfacine is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Rauwolfia serpentina root, Reserpine, Deserpidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18065/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18051, 'Diamorphine', 'Metformin', 'Moderate', 'Morphine is a cationic drug and theoretically may decrease the excretion of metformin by competing for renal tubular transport. Although this interaction has not been specifically reported for morphine, cimetidine (also a cationic drug) has been reported to interact with metformin in this manner. Increased metformin levels may increase the risk of lactic acidosis.', NULL, 'If morphine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended.', NULL, 'If morphine and metformin must be used together, particularly slow and cautious titration of metformin dosage is recommended. The maximal dose of metformin probably also should be reduced until further information about this interaction is available. Patients should be advised to monitor their blood glucose and to promptly notify their physician if they experience possible signs of lactic acidosis such as malaise, myalgia, respiratory distress, hyperventilation, slow or irregular heartbeat, somnolence, abdominal upset, or other unusual symptoms.', 'Excretion', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Acamprosate, Lofexidine, Naltrexone, Disulfiram, Levacetylmethadol, Nalmefene, Buprenorphine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18066/', '[1] "Product Information. Glucophage (metformin)." Bristol-Myers Squibb, Princeton, NJ.[2] Somogyi A, Stockley C, Keal J, Rolan P, Bochner F "Reduction of metformin renal tubular secretion by cimetidine in man." 